import React, {useState} from "react";
import Card from "../UI/Card.js";
import classes from "./AddUser.module.css"
import Button from "../UI/Button.js";

function AddUser(props) {
    
    const initialFormValues = {
        username: '',
        age: '',
    }
    const [formValues, setFormValues] = useState(initialFormValues);
    
    function handleInputChange(event){
        setFormValues({
            ...formValues,
            [event.target.name]: event.target.value,
        })
    }

    function addUserHandler(event){
        event.preventDefault();
        setFormValues(initialFormValues);
    }

    return(
        <Card className={classes.input}>
            {/* This form should be output inside of the card, and the card component should output what's passed between the opening and closing tags off card.  */}
            <form onSubmit={addUserHandler}>
                <label htmlFor="username">Username</label>
                <input type="text" id="username" name="username" onChange={handleInputChange}/>
                <label htmlFor="age">Age</label>
                <input type="text" id="age" name="age" onChange={handleInputChange}/>
                <Button type="submit">Add user</Button>
            </form>
        </Card>
    )
}

export default AddUser;
